import notFound from '../assets/notfound.jpg'
import style from './Card.module.css'
import { useEffect, useState } from 'react';

const card = ({ name, price, about, categori, thumb }) => {

    const [textInstallments, setTextInstallments] = useState("");
    const [installments, setinstallments ] = useState(0);
    const [value, setValue] = useState(0);


    useEffect(() => {
       const calcInstallments = () =>{
        if (price >= 100) {
            setValue(price / 2)
            setinstallments(2)
        }else if (price > 150) {
            setValue(price / 3)
            setinstallments(3)
        } else if (price > 500) {
            setValue(price / 4)
            setinstallments(4)
        }else if (price > 1000) {
            setValue(price / 6)
            setinstallments(6)
        }
       }
    },[])

    return (
        <>
            <div className={style.card}>
                <div className={style.card_content_img}>
                {!thumb && 
                <img src={notFound} alt="picture_product" className={style.card_img} />
                }
                {thumb &&
                    <img src={thumb[0].img} alt={name} className={style.card_img} />
                }
                </div>
                <br />
                <h3 className={style.card_title}>{name}</h3><br />
                <hr />
                <p className="price"> R$ { price }</p><br />
                {/* Adicionar link para categorias semelhantes */}
            </div>
        </>
    )
}

export default card
