import { useEffect, useState } from 'react'
import carrouselStyle from './carrouselStyle.css'
import next from '../assets/next.png'
import preview from '../assets/preview.png'
import Card from './Card';


const Carrousel = ({ props: data }) => {

    const [currentIten, setCurrentIten] = useState(0);
    const [itens, setItens] = useState(null);
    const [maxItens, setMaxItens] = useState(0);

    useEffect(() => {
        
        const handleCarrousel = async () => {
            
            setItens(data);
            let max = data.length;
            setMaxItens(max);
        }
        handleCarrousel()
    }, [data])


    const handleLeftClick = () => {
        if (currentIten <= 0) {
             setCurrentIten(maxItens - 1);
        console.log(currentIten);
    } else {
             setCurrentIten(currentIten - 1);
        console.log(currentIten);
    }
    }

    const handlerigthClick = () => {
        if (currentIten >= maxItens -1) {
           // sunOrSub(value)
             setCurrentIten(0);
            console.log("entrou")
            console.log("ini:" + currentIten)
        } else {
             setCurrentIten(currentIten + 1)
        }
        console.log(currentIten);
        console.log("handlerigthClick");
    }

  /*  const sunOrSub = (v) =>{
        if (v === 0) {
            
        }
    }

    const haandleCenterIten = (index) => {

    }
*/
    return (
        <div className="container" style={carrouselStyle}>
            <button className='arrow-left control' onClick={() => handleLeftClick}><img src={next} alt="next" /></button>
            <button className='arrow-right control' onClick={handlerigthClick}><img src={preview} alt="preview" /></button>
            <div className='gallery-wrapper'>
                <div className='gallery'>
                    {data && data.map((p) => (
                        <div className='iten current-iten' key={p.id}>
                            <Card name={p.name} price={p.price} about={p.about} categori={p.categori} thumb={p.pictures} />
                        </div>
                    ))}
                </div>
            </div>
        </div>

    )
}

export default Carrousel
