import imgError from './assets/erro.gif'
import imgLoad from './assets/load.gif'
import logoSelf from './assets/logo4.png'
import { useFetch } from '../hook/useFetch.js';
import Carrousel from '../components/Carrousel'
import CarrousselSales from '../components/CarrousselSales';


const Home = () => {
    let urlProducts = "http://localhost:3000/products";
    let urlSales = "http://localhost:3000/Sales";
    const { data: produtos, load, error } = useFetch(urlProducts);
    const { data: sales, load: load_sales, error: error_sales } = useFetch(urlSales);
    return (
        <div>
            <div className="header">
                <img src={logoSelf} alt="logo" className='logo' />
                <div className='promotional'>

                    {error_sales ? (<p> <img src={imgError} alt="error" style={{ width: "50px" }} />  {error_sales}</p>) : (
                        load_sales ? (<img src={imgLoad} alt="Loading" style={{ width: "50px" }} />) : (
                            <div>
                                <CarrousselSales props={sales} />
                            </div>

                        )
                    )}
                </div>
            </div>
            <br />
            <hr />
            {error ? (<p> <img src={imgError} alt="error" style={{ width: "50px" }} />  {error}</p>) : (
                load ? (<img src={imgLoad} alt="Loading" style={{ width: "50px" }} />) : (
                    <div>
                        <hr />
                        <Carrousel props={produtos} />
                    </div>
                )
            )}
        </div>
    )
}

export default Home
