import notFound from '../assets/notfound.jpg'
import style from './Card.module.css'

const card = ({name, price, about, categori, thumb}) => {
    return (
        <div className={style.cardform}>
            {thumb && <img src={notFound} alt="picture_product" />}
            {thumb &&
                <ul className="Picture-main">
                    {thumb.map((picture) => (
                        <li key={picture.id}>
                            <img src={picture.img} alt={name} />
                        </li>
                    ))}
                </ul>
            }
            <br />
            <h5>{name}</h5><br />
            <p><strong>Descrição: </strong>{about}</p><br />
            <br />
            <hr />
            <p className='price'><strong>Preço: </strong> {price}</p><br />
            {/* Adicionar link para categorias semelhantes */}
        </div>
    )
}

export default card
