import './App.css';
import imgError from './assets/erro.gif'
import imgLoad from './assets/load.gif'
//import { useEffect } from 'react';
//import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { useFetch } from './hook/useFetch.js';
import Carrousel from '../src/components/Carrousel.js';
import CarrousselSales from './components/CarrousselSales';

function App() {
  let url = "http://localhost:3000/products";
  const { data: produtos, load, error } = useFetch(url);

  return (
    <div className="App">
      <header>
      <h1>Jo√£o MarkUp</h1>
      </header>
      <body>
      {error ? (<p> <img src={imgError} alt="error" style={{ width: "50px" }} />  {error}</p>) : (
        load ? (<img src={imgLoad} alt="Loading" style={{ width: "50px" }} />) : (
          <CarrousselSales props={produtos}/>
        )
        )}

        < >
        {error ? (<p> <img src={imgError} alt="error" style={{ width: "50px" }} />  {error}</p>) : (
        load ? (<img src={imgLoad} alt="Loading" style={{ width: "50px" }} />) : (
          <Carrousel props={produtos}/>
        )
        )}
      </>
      </body>

    </div>
  );
}

export default App;
