import { useEffect, useState, useRef } from 'react'
import carrouselStyle from './carrouselStyle.css'
import next from '../assets/next.png'
import preview from '../assets/preview.png'
import Card from './Card';


const Carrousel = ({ props: data }) => {

    const [itens, setItens] = useState(null);
    const [maxItens, setMaxItens] = useState(0);
    const [currentIten, setCurrentIten] = useState(1);
    let [selected] = useState("iten current-iten");
    let [unselected] = useState("iten");
    const selectedReference = useRef();
    const window = useRef(null);

    useEffect(() => {
        const handleWindow = async () => {
            if (data) {
                setItens(data);
                setMaxItens(data.length);
                console.log(data);
                setCurrentIten(1)
            } else {
                setItens(null);
                setMaxItens(0);
            }
        }
        handleWindow();
    }, [window]);

    useEffect(() => {
        const handleCarrousel = async () => {
            if (data) {
                setItens(data);
                setMaxItens(data.length);
                console.log(data);
                setCurrentIten(1)
            } else {
                setItens(null);
                setMaxItens(0);
            }
        }
        handleCarrousel();

    }, [data]);

    useEffect(() => {
        const updateiten = () => {
            if (currentIten > maxItens) {
                setCurrentIten(1);
            } else if(currentIten < 1) {
                setCurrentIten(maxItens);
            }
        }

        const onIndexIten = async () => {
            await selectedReference.current?.scrollIntoView({ behavior: 'smooth', inline: "center" })
        }
        onIndexIten();
        updateiten();
    }, [currentIten, maxItens]);

    const handleLeftClick = () => {
        setCurrentIten(currentIten - 1);
        console.log(currentIten)
    }

    const handlerigthClick = () => {
        setCurrentIten(currentIten + 1);
        console.log(currentIten);
    }

    return (
        <div className="container" style={carrouselStyle} ref={window}>
            <button className='arrow-left control' onClick={handleLeftClick}><img className='img-arrow-left' src={next} alt="next" /></button>
            <button className='arrow-right control' onClick={handlerigthClick}><img className='img-arrow-right' src={preview} alt="preview" /></button>
            <div className='gallery-wrapper'>
                <div className='gallery'>
                    {itens && itens.map((p) => (
                        <div
                            className={p.id === currentIten ? selected : unselected}
                            ref={p.id === currentIten ? selectedReference : null}
                            key={p.id}>
                            <Card name={p.name} price={p.price} about={p.about} categori={p.categori} thumb={p.pictures} />
                        </div>
                    ))}
                </div>
            </div>
        </div>

    )
}

export default Carrousel
