import React from 'react'
import style from './Heeader.css'
import { useState, useEffect } from 'react'
import logoSelf from '../assets/logo4.png'
import CarrousselSales from '../components/CarrousselSales';

const Header = ({prop: Purl, componente}) => {
    
    const [url, setUrl] = useState(Purl);
    const [component, setComponent] = useState("false");

    useEffect(() => {
        loadHeader = async () =>{
            setUrl(Purl);
            setComponent(component)
        }
        loadHeader();
    },[url, component])

  return (
    <div style={style} className="header">
    {component && <img src={logoSelf} alt="logo" className='logo' />}
    <div className='promotional'>
                <>
                { component && <CarrousselSales props={url} />}
                </>
    </div>
</div>
  )
}

export default Header
