import './App.css';
import imgError from './assets/erro.gif'
import imgLoad from './assets/load.gif'
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { useFetch } from './hook/useFetch.js';
import {card} from './components/card';

function App() {
  let url = "http://localhost:3000/";
  const {data: produtos, load, error } = useFetch(url);



  return (
    <div className="App">
      <h1>Jo√£o MarkUp</h1>
      <BrowserRouter>
        <Routes>

        </Routes>
      </BrowserRouter>
      <div onLoad={useFetch}>
      {error && <p> <img src={imgError} alt="error" style={{ width: "50px" }} />  {error}</p>}
      {load && <img src={imgLoad} alt="Loading" style={{ width: "50px" }} />}
      {!load &&
        <div>
          <ul>
            {produtos && produtos.map((product) => (
              <>
              <card iten={product}/>
              </>
            ))}
          </ul>
        </div>
      }
      </div>
    </div>
  );
}

export default App;
